package com.lynxwork.erp.security.domain;import java.io.Serializable;import java.util.List;import javax.persistence.Column;import javax.persistence.Entity;import javax.persistence.GeneratedValue;import javax.persistence.GenerationType;import javax.persistence.Id;import javax.persistence.NamedQueries;import javax.persistence.NamedQuery;import javax.persistence.OneToMany;import javax.persistence.SequenceGenerator;import javax.persistence.Table;@Entity
@NamedQueries({@NamedQuery(name = "User.findAll",query = " select o from User o"),@NamedQuery(name = "User.findByPropertyName",query = " select o from User o   where  name = :name"),@NamedQuery(name = "User.findByPropertyPassword",query = " select o from User o   where  password = :password"),@NamedQuery(name = "User.findByRangeName",query = "  select o from User o where name between :nameInitVal  and  :nameEndVal"),@NamedQuery(name = "User.findByRangePassword",query = "  select o from User o where password between :passwordInitVal  and  :passwordEndVal"),})
@Table(name = "USER")
 public class User implements Serializable { /*********************************************************************************************                                  PROPERTY ENTITY DEFINITION                               **********************************************************************************************/@Id@GeneratedValue( strategy = GenerationType.IDENTITY )@Column( name = "USER_ID" )private Long userId;@Column(name="NAME", nullable = false, length = 20) private String name;@Column(name="PASSWORD", nullable = false, length = 14) private String password;/*********************************************************************************************                                  PROPERTY ACCESSORS DEFINITION                            *********************************************************************************************/public String getName(){return this.name;}public String getPassword(){return this.password;}/*********************************************************************************************                                  PROPERTY MUTATORS DEFINITION                             **********************************************************************************************/public  void setName(String name){this.name = name;}public  void setPassword(String password){this.password = password;} } 